import './crd.css'
import React, { useState,useEffect } from 'react'
import axios from '../../Static/axios'
import {img_url} from '../../Static/Static'
import {API_KEY} from '../../Static/Static'
import YouTube from 'react-youtube';


function Card(props) {
  const [movie,setMovie] = useState([])
  const [youId,setyouId] = useState('')
  useEffect(() => {
    axios.get(props.url).then((Response)=>{
      console.log(Response.data.results)
      setMovie(Response.data.results)
    })
  }, [])
  const myfunction=(id)=>{
    console.log("clicked")
    console.log(id)
      axios.get(`/movie/${id}/videos?api_key=${API_KEY}&language=en-US`).then((response)=>{
              console.log(response.data.results[0])
              setyouId(response.data.results[0].key)

      })
  }
  const opts = {
    height: '390',
    width: '640',
    playerVars: {
      // https://developers.google.com/youtube/player_parameters
      autoplay: 0,
    },
  };  
  return (
    
    <div className='card'>
      <h1 className='title'>{props.title}</h1>
        <div className='crd1'>
        {
          movie.map((obj)=>
              <img onclick={()=>myfunction(obj.id)} className={props.small ? 'small' : 'img'} src={`${img_url+obj.backdrop_path}`}/>
          )
        }
        </div>
    {youId &&  <YouTube videoId={youId.key} opts={opts}/>}
    </div>
  )
}

export default Card
